	public ExecutaPessoalPlus() {}
	
	public PessoalPlus executar(PessoalPlus pesPlus, Boolean xml) {
		
		if (!critica(pesPlus)) {
			pesPlus.setSenha("");
			return pesPlus;
		}
		
		log = new Log(false, GlobalConstants.PASTALOG);
		protocolo = Util.geraProtocolo();	
		resposta = "";
		
		//Criptografa a senha;
		pesPlus.setSenha(Util.converteMD5(pesPlus.getSenha(), protocolo, log));
		
		String ipRemoto    = GlobalConstants.IPREMOTO;
		boolean isCache    = false;
		String codigo      = pesPlus.getCodigo();
        String senha       = pesPlus.getSenha();
		String produto     = "73";
		String solicitacao = "Produto="+produto+";Codigo="+pesPlus.getCodigo()+";Senha="+pesPlus.getSenha()+";CPF="+pesPlus.getCpf();
				
		resposta = cache.cacheConsulta(solicitacao, protocolo, log); // Verifica cache
		
		if (resposta.trim().length() > 0) { // verifica se o cache respondeu
			isCache = true;
		}

		if (!isCache) { // Se nao foi respondido pelo cache da procedimento.
			log.registro(protocolo, "SND", solicitacao);
			
			//Fazer logon - verificar codigo, senha e se o produto pode ser consultado.
			Autenticacao aut = new Autenticacao();
			String strLogon = aut.logon(codigo, senha, produto, protocolo);
			if(!strLogon.equals("OK")) {
				cache.apagaCache(protocolo);
				log.registro(protocolo, "RCV", strLogon);
				pesPlus.setStatusRetorno((byte) 1);
				pesPlus.setMensagemRetorno(strLogon);				
				return pesPlus;
			}
						
			//Fazer a consulta nos BUREAU 			
			pesPlus = consultaCDLRio(pesPlus);	
			
			if(pesPlus.getSpcaxml().getRESPOSTA().getRESPOSTARETORNO().getSTATUSRESPOSTA().equals("0")) {
				//Fazer a bilhetagem					
				Bilhetagem bilhetar = new Bilhetagem();
				//bilhetar.bilhetar(GlobalConstants.idCliente, pesPlus.getCpf(), produto, ipRemoto, protocolo);
			}
			
			//Atualizo a mensagem que veio do CDLRio para o WSOrpec
			pesPlus.setStatusRetorno((byte) Integer.parseInt(pesPlus.getSpcaxml().getRESPOSTA().getRESPOSTARETORNO().getSTATUSRESPOSTA()));
			pesPlus.setMensagemRetorno(pesPlus.getSpcaxml().getRESPOSTA().getRESPOSTARETORNO().getMENSAGEMRESPOSTA());
			
			//Limpo as tag <S-CODIGO>, <S-SENHA>, <S-SOLICITANTE> e <ASSOCIADO-SOLICITANTE> 
			resposta = Util.gravaTag("<S-CODIGO>", "0", resposta);
			resposta = Util.gravaTag("<S-SENHA>", "0", resposta);
			resposta = Util.gravaTag("<S-SOLICITANTE>", "", resposta);
			resposta = Util.gravaTag("<ASSOCIADO-SOLICITANTE>", "", resposta);
						
			//Retiro informacoes da tag <SPCA-XML>
			resposta = Util.limpaXML(resposta);
			
			if(xml){pesPlus.setXml(resposta);}// Quando a resposta retorna só o xml
			cache.cacheResposta(resposta, protocolo);
			log.registro(protocolo, "RCV", resposta);
		}else{// Se foi respondido pelo cache
			InputStream is = null;
			BufferedReader br = null;
			SPCAXML obj = new SPCAXML();
			
			try {
			is = new ByteArrayInputStream(resposta.getBytes());
			br = new BufferedReader(new InputStreamReader(is));
			
			//Faco o parse aqui xml->classe java				
			JAXBContext jaxbContext = JAXBContext.newInstance(SPCAXML.class);
			Unmarshaller jaxbUnmarshaller = jaxbContext.createUnmarshaller();
			obj = (SPCAXML) jaxbUnmarshaller.unmarshal(br);

			pesPlus.setSpcaxml(obj);
			if(xml){pesPlus.setXml(resposta);}// Quando a resposta retorna só o xml

			//Atualizo a mensagem que veio do CDLRio para o WSOrpec
			pesPlus.setStatusRetorno((byte) Integer.parseInt(pesPlus.getSpcaxml().getRESPOSTA().getRESPOSTARETORNO().getSTATUSRESPOSTA()));
			pesPlus.setMensagemRetorno(pesPlus.getSpcaxml().getRESPOSTA().getRESPOSTARETORNO().getMENSAGEMRESPOSTA());
			
			is.close();
			br.close();
			
			} catch (JAXBException e) {
				log.registro(protocolo, "ERRO", e.getMessage());
				pesPlus.setStatusRetorno((byte) 1);
				pesPlus.setMensagemRetorno("Erro no parse do cache para o objeto");						
			} catch (IOException e) {
				log.registro(protocolo, "ERRO", e.getMessage());
				pesPlus.setStatusRetorno((byte) 1);
				pesPlus.setMensagemRetorno("Erro no parse do cache para o objeto");						
			}
		}
		return pesPlus;
	}

	public boolean critica(PessoalPlus pesPlus){
		
		if(Util.Empty(pesPlus.getCodigo())){
			pesPlus.setStatusRetorno((byte) 1);
			pesPlus.setMensagemRetorno("Codigo nao informado");
			return false;
		}else{
			if(!Util.isdigit(pesPlus.getCodigo())){
				pesPlus.setStatusRetorno((byte) 1);
				pesPlus.setMensagemRetorno("Codigo invalido");				
				return false;
			}
		}
		if(Util.Empty(pesPlus.getSenha())){
			pesPlus.setStatusRetorno((byte) 1);
			pesPlus.setMensagemRetorno("Senha nao informada");				
			return false;
		}
		
		if (Util.Empty(pesPlus.getCpf())) {
			pesPlus.setStatusRetorno((byte) 1);
			pesPlus.setMensagemRetorno("CPF nao informado");				
			return false;			
		}else {
			if (!Util.IsCPF(pesPlus.getCpf())) {
				pesPlus.setStatusRetorno((byte) 1);
				pesPlus.setMensagemRetorno("CPF invalido");				
				return false;
			}
		}
		return true;
	}
	
